import React from 'react';
//import { Scheduler, SchedulerViews, SchedulerDataSource, SchedulerHeader } from "smart-webcomponents-react";    //presence or absence of this line has no effect 


import { Accordion, AccordionItem } from 'smart-webcomponents-react/accordion';//for calendar -- working fine
import 'smart-webcomponents-react/source/styles/smart.default.css';//for calendar -- working fine


const MyScheduler = () => {
    const dataSource = new SchedulerDataSource();
  
    // Configure the data source, events, and other properties
    // dataSource.data = ...;
    const currentDate = new Date();

    const renderCustomHeader = () => {
      return (
        <div>
          <h2>Custom Scheduler Header</h2>
          <button onClick={handleButtonClick}>Custom Button</button>
        </div>
      );
    };
  
    const handleButtonClick = () => {
      // Handle button click event
      console.log('Custom button clicked');
    };

    const Calendar = () => {   //for calendar--working fine
      return (
        <Accordion>  
        <AccordionItem label="First Item">First Item Content.</AccordionItem>  
        <AccordionItem label="Second Item">Second Item Content.</AccordionItem>  
        <AccordionItem label="Third Item">Third Item Content.</AccordionItem>  
        <AccordionItem label="Fourth Item">Fourth Item Content.</AccordionItem>  
        </Accordion> 
      ); 
    }
  
    return (
      <div>
        <Scheduler
        views={[SchedulerViews.Month, SchedulerViews.Week]}
        currentView={SchedulerViews.Month}
        currentDate={currentDate}
        dataSource={dataSource}
        showLegend={true}
        weekends={true}
      >
          <SchedulerHeader>
            {/* Add any custom header elements */}
          </SchedulerHeader>
        </Scheduler>
      </div>
    );
  };
  export default MyScheduler;
